-- ===============================================================
-- ACTIVITIES
-- ===============================================================
CREATE TABLE activities (
  activityId   VARCHAR(50) NOT NULL,
  title        VARCHAR(255) NOT NULL,
  description  TEXT,
  imageUrl     VARCHAR(1024),
  status       ENUM('active','inactive') DEFAULT 'active',
  createdAt    DATETIME DEFAULT CURRENT_TIMESTAMP,
  PRIMARY KEY (activityId),
  KEY idx_activities_status (status),
  KEY idx_activities_createdAt (createdAt)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- ===============================================================
-- BADGES
-- ===============================================================
CREATE TABLE badges (
  badgeId     VARCHAR(50) NOT NULL,
  badgeName   VARCHAR(255) NOT NULL,
  description TEXT,
  imageUrl    VARCHAR(1024),
  PRIMARY KEY (badgeId)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- ===============================================================
-- ADMINS
-- ===============================================================
CREATE TABLE admins (
  lineUserId VARCHAR(50) NOT NULL,
  PRIMARY KEY (lineUserId),
  KEY idx_admins_user (lineUserId),
  CONSTRAINT fk_admins_user
    FOREIGN KEY (lineUserId) REFERENCES users(lineUserId)
    ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

-- ===============================================================
-- SUBMISSIONS
-- ===============================================================
CREATE TABLE submissions (
  submissionId VARCHAR(50) NOT NULL,
  activityId   VARCHAR(50) NOT NULL,
  lineUserId   VARCHAR(50) NOT NULL,
  description  TEXT NOT NULL,
  imageUrl     VARCHAR(1024),
  status       ENUM('pending','approved','rejected') DEFAULT 'pending',
