  CONSTRAINT fk_submissions_user FOREIGN KEY (lineUserId) REFERENCES users(lineUserId) ON DELETE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

